          //     : r 5,  5,  5, r ,  ,  
          const normalizedForRegex = phraseWithoutEnergy.toLowerCase().replace(//g, 'r');
          const isR5WithSpace = /^(|r)\s*5\s*$|^(|r)\s*\s*$/i.test(normalizedForRegex);
          
