            .replace(/\s*\s*/g, '.')
            .replace(/,/g, '.')  //     
            .trim();
          
          //    201.x (201.5, 201.6, 201.7, 201.8)
          //  : "201.5", "201  5", "201,5", "201  5"
          const mercury201Pattern = /^201\s*[\.]\s*([5-8])$/;
          const mercury201Match = normalizedForMercury.match(mercury201Pattern);
          if (mercury201Match) {
