    function generateQRCode(data, canvas) {
        //    ,   
        function useQRCodeLibrary() {
            //   qrcode (npm) - API: QRCode.toCanvas()
            if (typeof QRCode !== 'undefined' && QRCode.toCanvas) {
                QRCode.toCanvas(canvas, data, { width: 300 }, (error) => {
                    if (error) {
                        console.error('  QR-:', error);
                        canvas.parentElement.innerHTML = '<p style="color:red;">  QR-: ' + error.message + '</p>';
                    }
                });
                return true;
            }
            //   qrcodejs (davidshimjs) - API: new QRCode(element, options)
            if (typeof QRCode !== 'undefined' && typeof QRCode === 'function') {
                try {
                    //   canvas ( ,  CSS)
                    canvas.width = 300;
                    canvas.height = 300;
                    canvas.style.width = '300px';
                    canvas.style.height = '300px';
                    canvas.style.display = 'block';
                    canvas.style.margin = '0 auto';
                    
                    // qrcodejs   DOM,   div
                    const tempDiv = document.createElement('div');
                    tempDiv.style.position = 'absolute';
                    tempDiv.style.left = '-9999px';
                    tempDiv.style.top = '-9999px';
                    tempDiv.style.width = '300px';
                    tempDiv.style.height = '300px';
                    document.body.appendChild(tempDiv);
                    
                    //  QR-   
                    const qr = new QRCode(tempDiv, {
                        text: data,
                        width: 300,
                        height: 300,
                        colorDark: '#000000',
                        colorLight: '#ffffff',
                        correctLevel: QRCode.CorrectLevel.H
                    });
                    
                    //    canvas (qrcodejs  img )
                    const checkForImage = (attempts = 0) => {
                        const img = tempDiv.querySelector('img');
                        
                        if (img) {
                            const ctx = canvas.getContext('2d');
                            
                            const drawImageToCanvas = () => {
                                try {
                                    ctx.clearRect(0, 0, canvas.width, canvas.height);
                                    ctx.drawImage(img, 0, 0, 300, 300);
                                    document.body.removeChild(tempDiv);
                                } catch (error) {
                                    console.error('    canvas:', error);
                                    document.body.removeChild(tempDiv);
                                    canvas.parentElement.innerHTML = '<p style="color:red;">  QR-</p>';
                                }
                            };
                            
                            if (img.complete && img.naturalWidth > 0) {
                                //   
                                drawImageToCanvas();
                            } else {
                                //   
                                img.onload = () => {
                                    drawImageToCanvas();
                                };
                                img.onerror = () => {
                                    document.body.removeChild(tempDiv);
                                    canvas.parentElement.innerHTML = '<p style="color:red;">   QR-</p>';
                                };
                            }
                        } else if (attempts < 30) {
                            //    (qrcodejs   )
                            setTimeout(() => checkForImage(attempts + 1), 50);
                        } else {
                            document.body.removeChild(tempDiv);
                            canvas.parentElement.innerHTML = '<p style="color:red;">  QR-</p>';
                        }
                    };
                    checkForImage();
                    
                    return true;
                } catch (error) {
                    console.error('  QR-  qrcodejs:', error);
                    return false;
                }
            }
            return false;
        }
        
        // ,    
        if (useQRCodeLibrary()) {
            return;
        }
        
        //  ,     
        let waitAttempts = 0;
        const maxWaitAttempts = 10; // 1  
        
        function waitForExternalModules() {
            if (useQRCodeLibrary()) {
                return; //  !
            }
            
            waitAttempts++;
            if (waitAttempts < maxWaitAttempts) {
                setTimeout(waitForExternalModules, 100);
            } else {
                //    ,  CDN
                loadFromCDN();
            }
        }
        
        //      CDN (fallback)
        function loadFromCDN() {
            // CDN URLs   qrcodejs ( )
            //  qrcode npm     
            //  qrcodejs  davidshimjs -   
            const cdnUrls = [
                //  CDN  qrcodejs
                'https://cdn.jsdelivr.net/gh/davidshimjs/qrcodejs/qrcode.min.js',
                //  CDN
                'https://cdnjs.cloudflare.com/ajax/libs/qrcodejs/1.0.0/qrcode.min.js',
                //   
                'https://unpkg.com/qrcodejs@1.0.0/qrcode.min.js'
            ];
            
            let currentCdnIndex = 0;
            
            function tryLoadNext() {
                if (currentCdnIndex >= cdnUrls.length) {
                    //  CDN  -    QR-  
                    canvas.parentElement.innerHTML = `
                        <div style="padding:20px; background:#f5f5f5; border-radius:5px;">
                            <p style="margin:0 0 10px 0; color:#666; font-weight:bold;">QR- .     .</p>
                            <p style="margin:10px 0; font-size:13px; color:#333;">  QR-     " ":</p>
                            <div style="background:#fff; padding:10px; border-radius:4px; margin:10px 0; font-family:monospace; font-size:12px;">
                                <div style="margin:5px 0;"><strong>:</strong> QRCode</div>
                                <div style="margin:5px 0;"><strong>URL:</strong> https://cdn.jsdelivr.net/gh/davidshimjs/qrcodejs/qrcode.min.js</div>
                            </div>
                            <p style="margin:5px 0; font-size:11px; color:#666;">:   qrcodejs ( qrcode npm),      .</p>
                            <p style="margin:10px 0 0 0; font-size:12px; color:#999;">   .</p>
                        </div>
                    `;
                    return;
                }
                
                const cdnUrl = cdnUrls[currentCdnIndex];
                
                const script = document.createElement('script');
                script.src = cdnUrl;
                script.crossOrigin = 'anonymous';
                
                script.onload = () => {
                    // ,  QRCode  
                    if (useQRCodeLibrary()) {
                        return; // !
                    } else {
                        //  ,  QRCode   -   CDN
                        currentCdnIndex++;
                        tryLoadNext();
                    }
                };
                
                script.onerror = (err) => {
                    console.warn(`[PhotoSync] Failed to load QRCode from CDN ${currentCdnIndex + 1} (${cdnUrl}), trying next...`);
                    currentCdnIndex++;
                    tryLoadNext();
                };
                
                document.head.appendChild(script);
            }
            
            tryLoadNext();
        }
        
        //    ,   CDN
        waitForExternalModules();
    }

    function startStatusPolling() {
        if (syncStatusInterval) {
